mapper.select:
SELECT * FROM( SELECT A.CNUM CNUM ,A.CSUBJECT CSUBJECT ,A.CNAME CNAME ,A.CCONTENT CCONTENT ,A.CFILE CFILE ,A.MEMNUM MEMNUM ,A.DELETEYN DELETEYN ,TO_CHAR(TO_DATE(A.INSERTDATE), 'YYYY-MM-DD') INSERTDATE ,TO_CHAR(TO_DATE(A.UPDATEDATE), 'YYYY-MM-DD') UPDATEDATE
,CEIL(ROW_NUMBER() OVER(ORDER BY A.CNUM)/ #{pageSize} ) pageSize ,COUNT(A.CNUM) OVER() AS totalCount FROM SFM_COMMUNITY A WHERE A.DELETEYN = 'Y' ORDER BY 1
) WHERE pageSize = #{curPage}
