mapper.select:
SELECT    
        A.AUTHCERTIFICATION       AUTHCERTIFICATION
       ,A.MEMEMAIL               MEMEMAIL
FROM    TEMP_AUTH A
WHERE    A.AUTHCERTIFICATION = #{authcertification}      
AND     A.MEMEMAIL  = #{mememail}

SELECT    
      A.PWCERTIFICATION       PWCERTIFICATION
      ,A.MEMID               MEMID
FROM    TEMP_PW A
WHERE    A.PWCERTIFICATION = #{pwcertification}   
AND      A.MEMID         = #{memid}
