mapper.update:
UPDATE SFM_NOTICE
SET 	NOTICENUM 				= #{noticenum,jdbcType=VARCHAR }		
		,NOTICETITLE 				= #{noticetitle,jdbcType=VARCHAR }
		,NOTICECONTENT				= #{noticecontent,jdbcType=VARCHAR }	
					
		,UPDATEDATE 			= SYSDATE 
WHERE 	DELETEYN ='Y'
AND    NOTICENUM = #{noticenum, jdbcType=VARCHAR}
